mapper.select:
SELECT carid as carId,carnumber as carNumber,cartype as carType,color,price
          , rentprice as rentPrice  ,deposit,isrenting,description
FROM  cars


        AND  carid = #{carId}


        AND  carNumber = #{carNumber}


        AND  carType = #{carType}


        AND  color = #{color}


        AND  isrenting = #{isrenting}


        AND  price = #{price}


        AND  rentprice = #{rentPrice}


        AND  deposit = #{deposit}
